import {clickCell} from './moves';
import {IsVictory,IsDraw, } from './helper';

export const TicTacToe = {
  setup: () => ({ cells: Array(9).fill(null) }),
	turn: {
    moveLimit: 1,
  },
  moves: {
    clickCell,
  },
	endIf: (G, ctx) => {
    if (IsVictory(G.cells)) {
      return { winner: ctx.currentPlayer };
    }
    if (IsDraw(G.cells)) {
      return { draw: true };
    }
  },
};
